select s.student_ssn as ssn, coalesce(f.fund_source, ' ') + coalesce(f.fund_type, ' ') + coalesce(f.fund_gov_id, ' ') as type, f.fund_token as token, CASE WHEN sa.disbursed_amt > 0 THEN disbursed_amt WHEN sa.status IN ('A','P') AND (f.fund_disbursement = 'N' OR f.fund_disbursement IS Null) AND disbursed_amt = 0 THEN sa.actual_amt ELSE 0 END as amount into lcu_fads_amounts from student s join stu_award_year say on say.student_token = s.student_token join stu_award sa on say.stu_award_year_token = sa.stu_award_year_token join funds f on f.fund_token = sa.fund_ay_token join lcu_fads_students ss on say.stu_award_year_token = ss.stu_award_year_token;
select ssn, SUM(case when type like '[SI]G_' AND token IN (${_TOP10PCT}) then amount else 0 end) as TOP10PCT, SUM(case when type like 'IG_' AND token IN (${_TUIT_EXEMPT_WAIV}) then amount else 0 end) as TUIT_EXEMPT_WAIV, SUM(case when type like '[SIFOP]G_' AND token IN (${_CATEGORICAL_AID}) then amount else 0 end) as CATEGORICAL_AID, SUM(case when type like 'FGL' AND token IN (${_FEDERAL_PELL}) then amount else 0 end) as FEDERAL_PELL, -- 31a SUM(case when type like 'FGS' AND token IN (${_FEDERAL_SEOG})then amount else 0 end) as FEDERAL_SEOG, -- 31b SUM(case when type like 'SG_' AND token IN (${_TPEG_ON_CAMPUS}) then amount else 0 end) as TPEG_ON_CAMPUS, --31c PUBLIC check puborpriv SUM(case when type like 'FGT' AND token IN (${_SMART_GRANT}) then amount else 0 end) as SMART_GRANT, --31d PUBLIC SUM(case when type like 'SG_' AND token IN (${_TEG_PRIVATE_SCHL}) then amount else 0 end) as TEG_PRIVATE_SCHL, -- 31e PUBLIC SUM(case when type like 'SG_' AND token IN (${_LEAP}) then amount else 0 end) as LEAP, -- 31f SUM(case when type like 'SG_' AND token IN (${_HB3015_GRANTS}) then amount else 0 end) as HB3015_GRANTS, --31g SUM(case when type like '[PO]G_' AND token IN (${_PRIVATESCH}) then amount else 0 end) as PRIVATESCH, --31h PrivateSch SUM(case when type like '[IPO]G_' AND token IN (${_MERIT_BY_INST}) then amount else 0 end) as MERIT_BY_INST, --31i InstPrivateMerit SUM(case when type like '[SFO]G_' AND token IN (${_BYRD_SCHOLARSHIP}) then amount else 0 end) as BYRD_SCHOLARSHIP, -- 31j SUM(case when type like 'SG_' AND token IN (${_STATE_NURSE_SCHLR}) then amount else 0 end) as STATE_NURSE_SCHLR, -- 31k SUM(case when type like 'SG_' AND token IN (${_STUDENT_DEP_SCHLR}) then amount else 0 end) as STUDENT_DEP_SCHLR, --31l PUBLIC SUM(case when type like 'FGA' AND token IN (${_ACG}) then amount else 0 end) as ACG, --31m SUM(case when type like 'SG_' AND token IN (${_OTHER_SCHLR_GRANT}) then amount else 0 end) as OTHER_SCHLR_GRANT, -- 31n SUM(case when type like 'SG_' AND token IN (${_TEXAS_GRANT_PGM}) then amount else 0 end) as TEXAS_GRANT_PGM, --31o SUM(case when type like 'SG_' AND token IN (${_TEOG}) then amount else 0 end) as TEOG, -- 31p PUBLIC SUM(case when type like 'SG_' AND token IN (${_FEDERAL_SLEAP}) then amount else 0 end) as FEDERAL_SLEAP, -- 31q SUM(case when type like 'FJW' then amount else 0 end) as FEDERAL_WK_STUDY, -- 32a SUM(case when type like 'SJ_' AND token IN (${_TEXAS_WORK_STUDY}) then amount else 0 end) as TEXAS_WORK_STUDY, -- 32b SUM(case when type like 'IJ_' AND token IN (${_NEED_BASED_INST}) then amount else 0 end) as NEED_BASED_INST, -- 32c SUM(case when type like 'FJO' AND token IN (${_AMERICORPS}) then amount else 0 end) as AMERICORPS, -- 32d SUM(case when type like 'SJ_' AND token IN (${_HB3015_WS}) then amount else 0 end) as HB3015_WS, --32e SUM(case when type like 'SJ_' AND token IN (${_TXWS_MENTORSHIP}) then amount else 0 end) as TXWS_MENTORSHIP, -- 32f SUM(case when type like 'FLG' AND token IN (${_SUBS_STAFFORD_LN}) then amount else 0 end) as SUBS_STAFFORD_LN, -- 33a SUM(case when type like 'FLU' AND token IN (${_UNSUB_STAFFORD_LN}) then amount else 0 end) as UNSUB_STAFFORD_LN, -- 33b --1330 SUM(case when type like 'FLN' then amount else 0 end) as PERKINS_LOAN, -- 33c SUM(case when type like 'FL[PB]' AND token IN (${_FEDERAL_PLUS_LNS}) then amount else 0 end) as FEDERAL_PLUS_LNS, -- 33d SUM(case when type like '[FS]L_' AND token IN (${_HELP}) then amount else 0 end) as [HELP], -- 33e SUM(case when type like 'SL_' AND token IN (${_COLLEGE_ACCESS_LN}) then amount else 0 end) as COLLEGE_ACCESS_LN, -- 33f SUM(case when type like '[ISF]L_' AND token IN (${_PRIM_CARE_STD_LN}) then amount else 0 end) as PRIM_CARE_STD_LN, -- 33g SUM(case when type like 'FL[PB]' AND token in (${_FDSLP_PLUS}) then amount else 0 end) as FDSLP_PLUS, -- 33h SUM(case when type like 'FLG' AND token IN (${_FDSLP_SUB}) then amount else 0 end) as FDSLP_SUB, -- 33i SUM(case when type like '[IPO]L_' AND token IN (${_OTHER_LT_LOANS}) then amount else 0 end) as OTHER_LT_LOANS, -- 33j --1386 SUM(case when type like 'FLU' AND token IN (${_UNSUB_FED_DIR_LNS}) then amount else 0 end) as UNSUB_FED_DIR_LNS, -- 33k SUM(case when type like 'SL_' AND token IN (${_BOT}) then amount else 0 end) as BOT, -- 33l SUM(case when type like 'SL_' AND token IN (${_HB3015_LOANS}) then amount else 0 end) as HB3015_LOANS, -- 33m SUM(case when type like 'FGS' AND token IN (${_TEACH}) then amount else 0 end) as TEACH, -- 34a SUM(case when type like '[SO]L_' AND token IN (${_HB3015_OTHER}) then amount else 0 end) as HB3015_OTHER, -- 34b 0 as ENROLL_ADJ_COA, -- 35a 0 as ENROLL_ADJ_TFC -- 35b into lcu_fads_totals from lcu_fads_amounts group by ssn;
select s.student_ssn as SOC_SEC_NUMBER, s.last_name as last_name, s.first_name as first_name, s.middle_init as middle_init, s.alternate_id as alt_id, case WHEN sfm.dependency_status IN ('I','Y') then isnull(isnull(sfm.agi, sffs.agi), 0) else 0 end as STDNT_SPOUSE_AGI, case WHEN sfm.dependency_status IN ('I','Y') then 0 else isnull(isnull(sfp.agi, sffp.agi), 0) end as PARENT_AGI, isnull( case when s.race=6 then '1' when s.race=4 then '2' when s.race=3 then '4' when s.race=2 then '5' when s.race=8 then '6' when s.race=9 then '7' when s.race=5 then '8' else null end, ${ethnic}) as RACE, case when sfm.data_valid='Y' then 1 else 2 end as ISIR_VALID, case when sfm.housing = 1 and max(say.award_year_token) >= 2009 then 2 when sfm.housing = 2 and max(say.award_year_token) >= 2009 then 1 when sfm.housing = 3 and max(say.award_year_token) >= 2009 then 3 else 1 end as HOUSING, isnull(case when s.hispanic = '1' then '2' when s.hispanic='2' then '1' else '3' end, case when s.hispanic like 'Y%' then '1' when s.hispanic like 'N%' then '2' else '3' end) as ETHNIC, case case when s.race=6 then '1' when s.race=4 then '2' when s.race=3 then '4' when s.race=2 then '5' when s.race=8 then '6' when s.race=9 then '7' when s.race=5 then '8' else ${ethnic} end when '1' then '1000000' when '2' then '0200000' when '4' then '0040000' when '5' then '0005000' when '6' then '0000600' when '7' then '0000070' when '8' then '0000008' end as RACE_CODE,
CASE WHEN (sfm.fisap_income IS NOT Null) THEN sfm.fisap_income else sfm.income end as STDNT_WORK_INCOME, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(spouse_income, 0) end as SPOUSE_WK_INCOME, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfm.soc_sec_benefits,0) end as SOC_SEC_BENEFITS, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfm.afdc,0) end as AFDC_ADC, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfm.child_supp_rcvd,0) end as CHILD_SUPPORT, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfm.other_untax_income,0) end as OTHER_INCOME_BENE, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.father_income,0) end as FATHERS_WK_INCOME, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.mother_income,0) end as MOTHERS_WK_INCOME, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.soc_sec_benefits,0) end as PAR_SOC_SEC_BENE, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.afdc,0) end as PARENTS_AFDC_ADC, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.child_supp_rcvd,0) end as PARENT_CHILD_SUPP, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.other_untax_income,0) end as PAR_INCOME_BENEF, coalesce(${ethnic}, 7) as ETHNIC_ORIGIN, CASE WHEN (sfm.first_bachelor_by_7_1_96 = 'N' or sfm.first_bachelor_by_7_1_96 IS NULL) AND sfm.grad_prof_in_96_97 = 'N' AND sfm.year_in_school <> 1 AND sfm.year_in_school < 6 THEN 1 WHEN (sfm.first_bachelor_by_7_1_96 = 'N' or sfm.first_bachelor_by_7_1_96 IS NULL) AND sfm.grad_prof_in_96_97 = 'N' AND sfm.year_in_school = 1 THEN 5 WHEN sfm.first_bachelor_by_7_1_96 = 'Y' AND sfm.grad_prof_in_96_97 = 'Y' AND (sfm.year_in_school > 5 OR sfm.year_in_school = 0) THEN 2 WHEN sfm.first_bachelor_by_7_1_96 = 'Y' AND sfm.grad_prof_in_96_97 = 'N' AND (sfm.year_in_school > 5 OR sfm.year_in_school = 0) THEN 4 ELSE 3 END as CLASSIFICATION, CASE WHEN sfm.dependency_status IN ('D','X') THEN '1' WHEN sfm.dependency_status IN ('I','Y') THEN '2' WHEN say.award_year_token > 2004 AND sfm.dependency_status IS NULL THEN '3' ELSE Null END as DEPENDENCY_STATUS, sfm.date_of_birth as DATE_OF_BIRTH, case (CASE WHEN dependency_status IN ('D','X') THEN coalesce(sfp.legal_residence,'TX') ELSE coalesce(sfm.legal_residence,'TX') END) when 'TX' then 1 else 2 end as RESIDENCY, user_int.value_02 as LOCATION_CODE, s.primary_street1 as STREET_ADDRESS, s.primary_city as CITY_ADDRESS, left(s.primary_zipcode, 5) + '0000' as ZIP_ADDRESS, --FIXME min(poe.load_this_term) as ENROLLMENT_STATUS, CASE WHEN sfm.are_you_male = 'Y' THEN 'M' WHEN sfm.are_you_male = 'N' THEN 'F' ELSE Null END as GENDER, CASE WHEN sfm.mother_highest_grade_level = ' ' THEN '4' ELSE coalesce(sfm.mother_highest_grade_level,'4') END as MOTHER_HIGH_GRADE, CASE WHEN sfm.father_highest_grade_level = ' ' THEN '4' ELSE coalesce(sfm.father_highest_grade_level,'4') END as FATHER_HIGH_GRADE, sasd.tot_budget as COST_OF_ATTEND, sfm.cc_tfc as TOTAL_FAMILY_CONT into lcu_fads_base_info from student s join stu_award_year say on say.student_token = s.student_token join stu_award sa on say.stu_award_year_token = sa.stu_award_year_token join funds f on f.fund_token = sa.fund_ay_token join say_fm_stu sfm on sfm.stu_award_year_token = say.stu_award_year_token join say_fm_par sfp on sfp.stu_award_year_token = say.stu_award_year_token join stu_ay_poe poe on poe.stu_award_year_token = say.stu_award_year_token join stu_ay_sum_data [sasd] on [sasd].stu_award_year_token = say.stu_award_year_token join say_fm_fnar_stu sffs on say.stu_award_year_token = sffs.stu_award_year_token join say_fm_fnar_par sffp on say.stu_award_year_token = sffp.stu_award_year_token left join user_int on say.stu_award_year_token = user_int.stu_award_year_token left join user_string on user_string.stu_award_year_token = say.stu_award_year_token join ( select stu_award_year_token from lcu_fads_students ) ss on say.stu_award_year_token = ss.stu_award_year_token group by s.student_ssn, s.last_name, s.first_name, s.middle_init, s.alternate_id, sfm.dependency_status, isnull(isnull(sfm.agi, sffs.agi), 0), isnull(isnull(sfp.agi, sffp.agi), 0), s.race, ${ethnic}, sfm.data_valid, sfm.housing, s.hispanic, CASE WHEN (sfm.fisap_income IS NOT Null) THEN sfm.fisap_income else sfm.income end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(spouse_income, 0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfm.soc_sec_benefits,0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfm.afdc,0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfm.child_supp_rcvd,0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfm.other_untax_income,0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.father_income,0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.mother_income,0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.soc_sec_benefits,0) end , CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.afdc,0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.child_supp_rcvd,0) end, CASE WHEN (sfm.fisap_income IS NOT Null) THEN null else coalesce(sfp.other_untax_income,0) end, coalesce(user_string.value_06, 7), CASE WHEN (sfm.first_bachelor_by_7_1_96 = 'N' or sfm.first_bachelor_by_7_1_96 IS NULL) AND sfm.grad_prof_in_96_97 = 'N' AND sfm.year_in_school <> 1 AND sfm.year_in_school < 6 THEN 1 WHEN (sfm.first_bachelor_by_7_1_96 = 'N' or sfm.first_bachelor_by_7_1_96 IS NULL) AND sfm.grad_prof_in_96_97 = 'N' AND sfm.year_in_school = 1 THEN 5 WHEN sfm.first_bachelor_by_7_1_96 = 'Y' AND sfm.grad_prof_in_96_97 = 'Y' AND (sfm.year_in_school > 5 OR sfm.year_in_school = 0) THEN 2 WHEN sfm.first_bachelor_by_7_1_96 = 'Y' AND sfm.grad_prof_in_96_97 = 'N' AND (sfm.year_in_school > 5 OR sfm.year_in_school = 0) THEN 4 ELSE 3 END, CASE WHEN sfm.dependency_status IN ('D','X') THEN '1' WHEN sfm.dependency_status IN ('I','Y') THEN '2' WHEN say.award_year_token > 2004 AND sfm.dependency_status IS NULL THEN '3' ELSE Null END, sfm.date_of_birth, CASE WHEN dependency_status IN ('D','X') THEN coalesce(sfp.legal_residence,'TX') ELSE coalesce(sfm.legal_residence,'TX') END, user_int.value_02, s.primary_street1, s.primary_city, s.primary_zipcode, CASE WHEN sfm.are_you_male = 'Y' THEN 'M' WHEN sfm.are_you_male = 'N' THEN 'F' ELSE Null END, CASE WHEN sfm.mother_highest_grade_level = ' ' THEN '4' ELSE coalesce(sfm.mother_highest_grade_level,'4') END, CASE WHEN sfm.father_highest_grade_level = ' ' THEN '4' ELSE coalesce(sfm.father_highest_grade_level,'4') END, sasd.tot_budget, sfm.cc_tfc;

